import * as rollup from 'rollup';
import { MarkRequired } from 'ts-essentials';

/** Array, or not yet */
declare type Arrayable<T> = T | Array<T>;
declare type Options = {
    entry?: string;
    outDir?: string;
    watch?: Arrayable<boolean | string>;
};
declare type NormalizedOptions = MarkRequired<Options, 'entry'>;

declare function runViteBuild(options: NormalizedOptions): Promise<rollup.RollupOutput | rollup.RollupOutput[] | rollup.RollupWatcher | undefined>;
declare function build(_options: Options): Promise<void>;

export { build, runViteBuild };
